definition.script:
pipeline {
    agent any
    stages {
        stage('Clone') {
            steps {
                git 'https://github.com//kafka-demo.git'
            }
        }
        stage('Run Producer') {
            steps {
                sh 'python3 producer.py'
            }
        }
        stage('Run Consumer') {
            steps {
                sh 'python3 consumer.py'
            }
        }
    }
}
